 export function myFunction() {

    let checked = false;
    var pingserver = new Image();
    pingserver.src = "https://www.cristalab.com/tutoriales/";

    console.log(pingserver.src);
    console.log(pingserver);
    console.log(pingserver.height);

    /*pingserver.onload = function(){
        // image  has been loaded
        alert("Online");
    };*/




    if (pingserver.height>0) {
        checked = true;
        alert("Online");
    } else {
        checked = false;
        alert("Offline");
    }

    return checked;  
}
